import React from "react";
import OwlCarousel from "react-owl-carousel";
import "owl.carousel/dist/assets/owl.carousel.css";
import "owl.carousel/dist/assets/owl.theme.default.css";
import Client1 from "../images/client1.jpg"; // Replace with correct path
import Client2 from "../images/client2.jpg"; // Replace with correct path

const ClientSection = () => {
  const clientFeedback = [
    {
      id: 1,
      feedback:
        "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam",
      name: "Moana Michell",
      subtitle: "magna aliqua",
      image: Client1,
    },
    {
      id: 2,
      feedback:
        "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam",
      name: "Mike Hamell",
      subtitle: "magna aliqua",
      image: Client2,
    },
  ];

  return (
    <section className="client_section layout_padding-bottom">
      <div className="container">
        <div className="heading_container heading_center psudo_white_primary mb_45">
          <h2>What Says Our Customers</h2>
        </div>
        <div className="carousel-wrap row">
          <OwlCarousel
            className="owl-carousel client_owl-carousel"
            loop
            margin={20}
            items={1}
            autoplay
            autoplayTimeout={3000}
            smartSpeed={1000}
          >
            {clientFeedback.map((client) => (
              <div className="item" key={client.id}>
                <div className="box">
                  <div className="detail-box">
                    <p>{client.feedback}</p>
                    <h6>{client.name}</h6>
                    <p>{client.subtitle}</p>
                  </div>
                  <div className="img-box">
                    <img
                      src={client.image}
                      alt={client.name}
                      className="box-img"
                    />
                  </div>
                </div>
              </div>
            ))}
          </OwlCarousel>
        </div>
      </div>
    </section>
  );
};

export default ClientSection;
