import React, { useState, useEffect } from "react";
import "../css/style.css"; // Ensure the CSS path is correct
import "../css/responsive.css"; // Ensure the CSS path is correct

const Menu = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [filter, setFilter] = useState("all");
  const [loading, setLoading] = useState(true);

  // Fetch menu items from a fake API
  useEffect(() => {
    const fetchMenuItems = async () => {
      try {
        const response = await fetch(
          "https://mocki.io/v1/9a6a5b20-b0b3-4a8c-97d6-2b6da8f32c94"
        ); // Replace with your mock API URL
        const data = await response.json();
        setMenuItems(data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching menu items:", error);
        setLoading(false);
      }
    };

    fetchMenuItems();
  }, []);

  // Filtered menu items based on category
  const filteredItems =
    filter === "all"
      ? menuItems
      : menuItems.filter((item) => item.category === filter);

  if (loading) {
    return <p>Loading menu items...</p>;
  }

  return (
    <section className="food_section layout_padding-bottom">
      <div className="container">
        <div className="heading_container heading_center">
          <h2>Our Menu</h2>
        </div>

        {/* Filters */}
        <ul className="filters_menu">
          {["all", "burger", "pizza", "pasta", "fries"].map((category) => (
            <li
              key={category}
              className={filter === category ? "active" : ""}
              onClick={() => setFilter(category)}
            >
              {category.charAt(0).toUpperCase() + category.slice(1)}
            </li>
          ))}
        </ul>

        {/* Menu Items */}
        <div className="filters-content">
          <div className="row grid">
            {filteredItems.map((item) => (
              <div
                key={item.id}
                className={`col-sm-6 col-lg-4 all ${item.category}`}
              >
                <div className="box">
                  <div>
                    <div className="img-box">
                      <img src={item.image} alt={item.name} />
                    </div>
                    <div className="detail-box">
                      <h5>{item.name}</h5>
                      <p>{item.description}</p>
                      <div className="options">
                        <h6>{item.price}</h6>
                        <a href="#">
                          <i className="fa fa-shopping-cart"></i>
                        </a>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* View More Button */}
        <div className="btn-box">
          <a href="#">View More</a>
        </div>
      </div>
    </section>
  );
};

export default Menu;
